import React from 'react'
import ReactDOM from 'react-dom'

const App = () => {
  const course = 'Half Stack application development'
  const parts = 
  [

  ]
   = {
    name: 'Fundamentals of React',
    exercises: 10
  }
  const part2 = {
    name: 'Using props to pass data',
    exercises: 7
  }
  const part3 = {
    name: 'State of a component',
    exercises: 14
  }
  const total = part1.exercises+part2.exercises+part3.exercises

  const Header = (props) => {
    return (
      <h1>{props.course}</h1>
    )
  }

  const Part = (props) => {
    return (
      <div>
        <h2>Part {props.num} is {props.part.name}</h2>
        <h3>It has {props.part.exercises} exercises</h3>
      </div>
    )
  }

  const Content = (props) => {
    return (
      <p>
        <Part num={1} part={part1}></Part>
        <Part num={2} part={part2}></Part>
        <Part num={3} part={part3}></Part>
      </p>
    )
  }

  const Total = (props) => {
    return (
      <h2>The total amount of exercises is {props.total}</h2>
    )
  }
  return (
    <>
      <Header course={course}></Header>
      <Content></Content>
      <Total total={total}></Total>
    </>
  )
}

ReactDOM.render(<App />, document.getElementById('root'))
