import React, {useState, useEffect} from 'react'
import axios from 'axios'

const Country = (props) => {
  return (
    props.country.name
  )
}

const Countries = (props) => {
  const [countries, setCountries] = useState([])

  useEffect(() => {
    axios.get('https://restcountries.eu/rest/v2/all')
      .then((response) => {
        console.log(response);
        setCountries(response.data)
      })
  }, [])

  function filterCountries() {
    return countries.filter((country) => country.name.toLowerCase().includes(props.input.toLowerCase()))
  }

  const cntrs = filterCountries()
  retu
  return (
    cntrs.length < 11 || !props.input ?
      filterCountries().map((country) => <li ><Country key={country.name} country={country}></Country></li>)
    :
    'Too many matches, specify another filter'
  )
}

export default Countries
